package com.jhcms.common.widget;import android.content.Context;import android.util.AttributeSet;import android.view.MotionEvent;import android.widget.ListView;/** * @author chenzheng * @ClassName: ListViewForScrollView * @Description: 自定义可适应ScrollView的ListView * @date 2014-6-27 上午11:45:10 */public class ListViewForListView extends ListView {    public ListViewForListView(Context context) {        super(context);    }    public ListViewForListView(Context context, AttributeSet attrs) {        super(context, attrs);    }    public ListViewForListView(Context context, AttributeSet attrs,                               int defStyle) {        super(context, attrs, defStyle);    }    @Override    /**     * 重写该方法，达到使ListView适应ScrollView的效果     */    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {        int expandSpec = MeasureSpec.makeMeasureSpec(Integer.MAX_VALUE >> 2,                MeasureSpec.AT_MOST);        super.onMeasure(widthMeasureSpec, expandSpec);    }    @Override    public boolean onTouchEvent(MotionEvent ev) {        return false;    }}